import registerBlockHelperMissing from 'handlebars/lib/handlebars/helpers/block-helper-missing';
import registerEach from 'handlebars/lib/handlebars/helpers/each';
import registerHelperMissing from 'handlebars/lib/handlebars/helpers/helper-missing';
import registerIf from 'handlebars/lib/handlebars/helpers/if';
import registerLog from 'handlebars/lib/handlebars/helpers/log';
import registerLookup from 'handlebars/lib/handlebars/helpers/lookup';
import registerWith from 'handlebars/lib/handlebars/helpers/with';

export function registerDefaultHelpers(instance) {
  registerBlockHelperMissing(instance);
  registerEach(instance);
  registerHelperMissing(instance);
  registerIf(instance);
  registerLog(instance);
  registerLookup(instance);
  registerWith(instance);
}

export function moveHelperToHooks(instance, helperName, keepHelper) {
  if (instance.helpers[helperName]) {
    instance.hooks[helperName] = instance.helpers[helperName];
    if (!keepHelper) {
      delete instance.helpers[helperName];
    }
  }
}
